

let quizQuestions=[];
let quizIndex=0;
let quizScore=0;
let quizTotal=0;

/* =========================
   SEARCH CURRENT WORD
========================= */

function searchWord(){

    const word = document
        .getElementById("arabic-word")
        .innerText
        .trim();

    if(!word) return;

    const url =
        "https://quran.com/bn/search?page=1&query="
        + encodeURIComponent(word);

    window.open(url,"_blank");
}


/* =========================
   START QUIZ MODE
========================= */

function startQuizMode(){

    let count = prompt(
        "ржХрждржЯрж┐ ржкрзНрж░рж╢рзНржи ржЦрзЗрж▓ржмрзЗржи? (10-100)",
        "10"
    );

    if(!count) return;

    count = parseInt(count);

    if(isNaN(count) || count<1) return;

    quizTotal =
        Math.min(count,quranData.length);

    quizQuestions =
        [...quranData]
        .sort(()=>Math.random()-0.5)
        .slice(0,quizTotal);

    quizIndex=0;
    quizScore=0;

    /* HIDE TOP STATS */
    document
        .getElementById("topStats")
        .style.display="none";

    document
        .querySelector(".word-card")
        .style.display="none";

    document
        .querySelector(".ayat-section")
        .style.display="none";

    document
        .querySelector(".progress-box")
        .style.display="none";

    document
        .querySelector(".navigation")
        .style.display="none";

    document
        .getElementById("quizSection")
        .style.display="block";

    renderQuiz();
}


/* =========================
   RENDER QUIZ
========================= */

function renderQuiz(){

    let q =
        quizQuestions[quizIndex];

    let options=[q.trans];

    let others =
        [...quranData]
        .filter(x=>x.trans!==q.trans)
        .sort(()=>Math.random()-0.5);

    for(let i=0;i<3;i++)
        options.push(others[i].trans);

    options.sort(()=>Math.random()-0.5);

    let html = `
        <div class="quiz-container">
            <div class="quiz-question">
                ${q.word}
            </div>

            <div class="quiz-options">
                ${options.map(opt=>
                    `<button onclick="checkAnswer(this,'${opt}','${q.trans}')">
                        ${opt}
                    </button>`
                ).join("")}
            </div>

            <div style="margin-top:15px;text-align:center;font-size:13px;">
                ржкрзНрж░рж╢рзНржи ${quizIndex+1}/${quizTotal}
                | рж╕рзНржХрзЛрж░ ${quizScore}
            </div>
        </div>
    `;

    document
        .getElementById("quizSection")
        .innerHTML=html;
}


/* =========================
   CHECK ANSWER
========================= */

function checkAnswer(btn,selected,correct){

    let buttons =
        document.querySelectorAll(
            ".quiz-options button"
        );

    buttons.forEach(b=>{
        b.disabled=true;
        if(b.innerText===correct)
            b.style.background="#2ecc71";
    });

    if(selected!==correct){
        btn.style.background="#e74c3c";
    }else{
        quizScore++;
    }

    setTimeout(()=>{
        quizIndex++;

        if(quizIndex<quizTotal){
            renderQuiz();
        }else{
            showResult();
        }

    },1000);
}


/* =========================
   SHOW RESULT
========================= */

function showResult(){

    document
        .getElementById("quizSection")
        .innerHTML = `
        <div class="quiz-container">
            <h2 style="text-align:center;">
                ЁЯОЙ ржХрзБржЗржЬ рж╢рзЗрж╖
            </h2>

            <p style="text-align:center;">
                ржЖржкржирж╛рж░ рж╕рзНржХрзЛрж░:
                ${quizScore}/${quizTotal}
            </p><br><br>

            <center><button
                onclick="location.reload()"
                class="btn-next">
                ЁЯУЦ ржЖржмрж╛рж░ ржкрзЬрзБржи
            </button></center>
        </div>
    `;
}

